import React,{useRef,useEffect,useState} from 'react';
import {select} from 'd3';
import * as d3 from 'd3'




function CircularBarPlot() {

const chartdata = [{'Country':'USA','Value':12534},{'Country':'Russia','Value':6148},{'Country':'Germany','Value':615}];
const [data,setData] = useState(chartdata);
const svgRef = useRef();
const width = 400;
const height = 400;
const margin = {top: 100, right: 0, bottom: 0, left: 0};
const innerRadius = 90;
const outerRadius = Math.min(width, height) / 2; 


useEffect(() => {
    const svg = select(svgRef.current)
    .attr("width", 700)
    .attr("height", 700)
    .append("g")
    .attr("transform", "translate(" + (width / 2 + margin.left) + "," + (height / 2 + margin.top) + ")");;

    const xScale = d3.scaleBand()
      .range([0, 2 * Math.PI])    // X axis goes from 0 to 2pi = all around the circle. If I stop at 1Pi, it will be around a half circle
      .align(0)                  // This does nothing
      .domain(data.map(() => {return data.Country}));

    const yScale = d3.scaleRadial()
    .range([innerRadius, outerRadius])   // Domain will be define later.
    .domain([0, 14000]); // Domain of Y is from 0 to the max seen in the data

    console.log("data : "+data)

    // Add the bars
    svg.append("g")
        .selectAll("path")
        .data([data])
        .enter()
        .append("path")
        .attr("fill", "#69b3a2")
        .attr("d", d3.arc()     // imagine your doing a part of a donut plot
            .innerRadius(innerRadius)
            .outerRadius(function (d) { return yScale(d['Value']); })
            .startAngle(function (d) { return xScale(d.Country); })
            .endAngle(function (d) { return xScale(d.Country) + xScale.bandwidth(); })
            .padAngle(0.01)
            .padRadius(innerRadius)) 
}, [data]);

    


    return (
        <div style={{height:'400px',width:'400px'}}>
            <svg ref={svgRef}></svg>
        </div>
    )
}

export default CircularBarPlot;




