import React from 'react';
import { ResponsiveSankey } from '@nivo/sankey';
import data from './data.json';
import { Card } from 'react-bootstrap';

const sankeydata = data.data;

function SankeyChart() {
    return (
        <div style={{height:'400px',width:'100%'}}>
            <ResponsiveSankey
                linkContract={1}
                data={sankeydata}
                margin={{ top: 40, right: 160, bottom: 40, left: 100 }}
                align="justify"
                sort="descending"
                colors={{ scheme: 'category10' }}
                nodeOpacity={1}
                nodeThickness={18}
                nodeInnerPadding={3}
                nodeSpacing={24}
                nodeBorderWidth={0}
                nodeBorderColor={{ from: 'color', modifiers: [['darker', 0.8]] }}
                linkOpacity={0.5}
                linkHoverOthersOpacity={0.1}
                enableLinkGradient={true}
                labelPosition="outside"
                labelOrientation="horizontal"
                labelPadding={16}
                labelTextColor={{ from: 'color', modifiers: [['darker', 1]] }}
                legends={[
                    {
                        anchor: 'bottom-right',
                        direction: 'column',
                        translateX: 130,
                        itemWidth: 100,
                        itemHeight: 14,
                        itemDirection: 'right-to-left',
                        itemsSpacing: 2,
                        itemTextColor: '#999',
                        symbolSize: 14,
                        effects: [
                            {
                                on: 'hover',
                                style: {
                                    itemTextColor: '#000'
                                }
                            }
                        ]
                    }
                ]}
                nodeTooltip={node => {
                                    console.log("Node Info")
                                    console.log(node)
                                    return (
                                    <span>Node: {node.label}</span>
                                    )
                                }
                            }
                linkTooltip={link => {
                                    console.log("Link Info")
                                    console.log(link)
                                    return (
                                        <div>
                                            <Card>
                                                <Card.Header style={{backgroundColor:link.color}}>{link.source.prod_name}</Card.Header>
                                                <Card.Body>
                                                    <Card.Title>{link.source.id} {' -> '} {link.target.id}</Card.Title>
                                                    <Card.Text>
                                                        <span> Weighted Adherence: {(link.value/100).toFixed(3)}</span><br/>
                                                    </Card.Text>
                                                </Card.Body>
                                            </Card>
                                        </div>
                                    )
                                }
                            }
            />
        </div>
    )
}

export default SankeyChart
