import React,{useRef,useEffect,useState} from 'react';
import {select} from 'd3';




function CircularBarPlot() {

const chartdata = [{'Country':'USA','Value':12534},{'Country':'Russia','Value':6148},{'Country':'Germany','Value':615}];
const [data,setData] = useState([10,20,30,40]);
const svgRef = useRef();



useEffect(() => {
    const svg = select(svgRef.current);
    svg
    .selectAll("circle")
    .data(data)
    .join("circle")
    .attr('r', value => value)
    .attr('cx', value => value * 2)
    .attr('cy', value => value * 2)
    .attr('stroke', "yellow")
}, [data]);

    return (
        <div style={{height:'400px',width:'400px'}}>
            <svg ref={svgRef}></svg>
        </div>
    )
}

export default CircularBarPlot;
